import React, { useState } from "react";
import Logo from "../Assets/logo.svg";
import { HiOutlineBars3 } from "react-icons/hi2";
import { Box, Drawer, List, ListItem, ListItemButton, ListItemText } from "@mui/material";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [openMenu, setOpenMenu] = useState(false);

  const menuOptions = [
    { text: "Home", path: "/" },
    { text: "About", path: "/about" },
    { text: "Contact", path: "/contact" },
    { text: "Blog", subcategories: [
        { name: "Nutritional Food", path: "/blog/nutritional-food" },
        { name: "Do's and Don'ts", path: "/blog/dos-and-donts" },
        { name: "Health and Hygiene", path: "/blog/health-and-hygiene" },
        { name: "Play and Learning", path: "/blog/play-and-learning" },
        { name: "Parenting Tips", path: "/blog/parenting-tips" },
      ],
    },
    { text: "Shopping", path: "/shopping" },
  ];

  return (
    <nav className="navbar">
      <div className="nav-logo-container">
        <img className="logo" src={Logo} alt="logo" />
      </div>

      <div className="nav-links-container">
        {menuOptions.map((menu) =>
          menu.subcategories ? (
            <div className="dropdown" key={menu.text}>
              <Link to="#" className="dropdown-title">
                {menu.text}
              </Link>
              <div className="dropdown-content">
                {menu.subcategories.map((sub) => (
                  <Link key={sub.name} to={sub.path}>
                    {sub.name}
                  </Link>
                ))}
              </div>
            </div>
          ) : (
            <Link key={menu.text} to={menu.path}>
              {menu.text}
            </Link>
          )
        )}
      </div>

      <div className="nav-menu-container">
        <HiOutlineBars3 onClick={() => setOpenMenu(true)} />
      </div>

      <Drawer open={openMenu} onClose={() => setOpenMenu(false)} anchor="right">
        <Box sx={{ width: 250 }} role="presentation" onClick={() => setOpenMenu(false)}>
          <List>
            {menuOptions.map((menu) => (
              <ListItem key={menu.text} disablePadding>
                {menu.subcategories ? (
                  <div className="drawer-dropdown">
                    <ListItemText primary={menu.text} />
                    <div className="drawer-submenu">
                      {menu.subcategories.map((sub) => (
                        <ListItemButton key={sub.name} component={Link} to={sub.path}>
                          <ListItemText primary={sub.name} />
                        </ListItemButton>
                      ))}
                    </div>
                  </div>
                ) : (
                  <ListItemButton component={Link} to={menu.path}>
                    <ListItemText primary={menu.text} />
                  </ListItemButton>
                )}
              </ListItem>
            ))}
          </List>
        </Box>
      </Drawer>
    </nav>
  );
};

export default Navbar;
