import React from "react";
import "./Blog.css";

const Blog = () => {
  const categories = [
    {
      name: "Nutrition",
      url: "/blogs/nutrition",
      description: "Discover tips and guides for healthy eating and balanced nutrition.",
    },
    {
      name: "Grooming Tips",
      url: "/blogs/grooming-tips",
      description: "Learn about skincare, haircare, and overall grooming routines.",
    },
    {
      name: "Health",
      url: "/blogs/health",
      description: "Get expert insights on maintaining your health and well-being.",
    },
    {
      name: "Fitness",
      url: "/blogs/fitness",
      description: "Stay fit with our workout tips, routines, and guides.",
    },
    {
      name: "Travel",
      url: "/blogs/travel",
      description: "Explore travel blogs with guides to destinations around the globe.",
    },
    {
      name: "Food",
      url: "/blogs/food",
      description: "Savor delicious recipes and food recommendations.",
    },
    {
      name: "Technology",
      url: "/blogs/technology",
      description: "Stay updated on the latest tech trends and innovations.",
    },
    {
      name: "Education",
      url: "/blogs/education",
      description: "Find resources and tips to excel in your educational journey.",
    },
    {
      name: "Lifestyle",
      url: "/blogs/lifestyle",
      description: "Get inspired by tips and ideas for a better lifestyle.",
    },
    {
      name: "Finance",
      url: "/blogs/finance",
      description: "Learn about managing finances, investments, and savings.",
    },
  ];

  return (
    <div className="blog-page-container">
      <h1>Explore Our Blog Categories</h1>
      <p className="blog-description">
        Dive into a variety of blogs covering topics from health and fitness to travel and technology.
      </p>
      <div className="blog-list">
        {categories.map((category, index) => (
          <div key={index} className="blog-card">
            <h3>{category.name}</h3>
            <p>{category.description}</p>
            <a href={category.url} className="blog-link">
              Explore Blogs
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
